function CatalogCtrl(a, c) {
    var b = this;
    b.part = {good_id: 1, quantity: 1};
    b.products = {};
    b.width_min = 0;
    b.width_max = 1000;
    b.height_min = 0;
    b.height_max = 1000;
    b.price_min = 0;
    b.price_max = 10000;
    b.name_like = "";
    b.getFilters = function () {
        return {
            width_min: b.width_min,
            width_max: b.width_max,
            height_min: b.height_min,
            height_max: b.height_max,
            price_min: b.price_min,
            price_max: b.price_max,
            name_like: b.name_like
        }
    };
    b.actionSearch = function () {c.post("/catalog/search", {filters: b.getFilters()}).then(function (d) {if (d.status == 200) {b.products = d.data}})};
    b.actionSearch()
}
var catalog = angular.module("catalog", []);
catalog.controller("CatalogCtrl", ["$scope", "$http", CatalogCtrl]);
catalog.filter("reverse", function () {return function (a) {return a.slice().reverse()}});